/** @jsx React.DOM */

jest.dontMock('../code/views/buttonView.jsx');

var React = require('react/addons');
var ButtonView = require('../code/views/buttonView.jsx');
var TestUtils = React.addons.TestUtils;

describe('ButtonView', function() {

  it('foo', function() {
    var buttonView = TestUtils.renderIntoDocument(
      <ButtonView />
    );

    var label = TestUtils.findRenderedDOMComponentWithTag(buttonView, 'button');
    expect(label.getDOMNode().textContent).toEqual('Our HX Button');

  });

});